package basics;

public class Days {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String daysOfWeek = "Tuesday";
		
		switch (daysOfWeek) {
		case "Monday" : 
			System.out.println("Today is " + daysOfWeek);
			break;
		case "Tuesday" : 
			System.out.println("Today is " + daysOfWeek);
			break;
		case "Wednesday" : 
			System.out.println("Today is " + daysOfWeek);
			break;
		}

	}

}
